import React, { useState,useEffect } from 'react';
import "./newsholder.css"

function Newsholder(){
    const [currentIndex, setCurrentIndex] = useState(0);

    const newsItems = [
        { text: 'News 1' },
        { text: 'News 2' },
        { text: 'News 3' }
    ];

    const showNews = (index) => {
        setCurrentIndex(index);
    };

    const nextNews = () => {
        setCurrentIndex((prevIndex) => (prevIndex + 1) % newsItems.length);
    };

    const prevNews = () => {
        setCurrentIndex((prevIndex) => (prevIndex - 1 + newsItems.length) % newsItems.length);
    }; 
    useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % newsItems.length);
    }, 3000); // Change news item every 3 seconds

    return () => clearInterval(interval); // Clean up the interval on component unmount
  }, []); // Empty dependency array to run effect o
    return(
      <div className="news-card">
        <div className="news-container">
          <div className="news-item active">
            <h2>{newsItems[currentIndex].text}</h2>
            <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
            <div className="button-container">
                <button className="arrow left" onClick={prevNews}>&lt;</button>
                <button className="arrow right" onClick={nextNews}>&gt;</button>
            </div>
          </div>
        </div>


      <div className="dots-container">
        {newsItems.map((item, index) => (
          <span
            key={index}
            className={`dot ${index === currentIndex ? 'active' : ''}`}
            onClick={() => showNews(index)}
          ></span>
        ))}
      </div>
    </div>
    )
}
export default Newsholder;
