import React, { useState } from 'react';
import '../App.css';

const Homepage = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const newsItems = [
    { title: 'Breaking News 1', content: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
    { title: 'Breaking News 2', content: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' },
    { title: 'Breaking News 3', content: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.' }
  ];

  const showNews = (index) => {
    setCurrentIndex(index);
  };

  const nextNews = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % newsItems.length);
  };

  const prevNews = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + newsItems.length) % newsItems.length);
  };

  return (
    <div className="homepage-container">
      <div className="news-card">
        <div className="news-container">
          {newsItems.map((item, index) => (
            <div
              key={index}
              className={`news-item ${index === currentIndex ? 'active' : ''}`}
            >
              <h2>{item.title}</h2>
              <p>{item.content}</p>
            </div>
          ))}
        </div>
      </div>

      <div className="button-container">
        <button className="arrow left" onClick={prevNews}>&lt;</button>
        <button className="arrow right" onClick={nextNews}>&gt;</button>
      </div>

      <div className="dots-container">
        {newsItems.map((item, index) => (
          <span
            key={index}
            className={`dot ${index === currentIndex ? 'active' : ''}`}
            onClick={() => showNews(index)}
          ></span>
        ))}
      </div>
    </div>
  );
};

export default Homepage;
