import { combineReducers } from "redux";

const loginReducer = (state = "" ,action) =>{
    switch(action.type){
        case 'LOG_IN':
            return true;
        case 'LOG_OUT':
            return "";
        default :
            return state
    }
}
const reducers = combineReducers({
    login:loginReducer,
})

export default reducers
