import { useState ,useContext} from "react";
import "./login.css";
import { Link } from "react-router-dom";
import { LoginContext } from "./LoginContext";

function Loginpage() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);

  const { isLoggedIn, handleLogin, handleLogout } = useContext(LoginContext);

  if (isLoggedIn) {
   return (
        <Link to="/" className="login-form-button">
            Siz Artıq 
          </Link>   
        )
  }
  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleShowPassword = () => {
    setShowPassword(!showPassword);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch("https://dummyjson.com/products");
      if (!response.ok) {
        setUsername("");
        setPassword("");
      } else {
        const data = await response.json();
        handleLogin();
        setUsername("");
        setPassword("");
        console.log(data);
        // Update the login state or perform other actions with the data
      }
    } catch (error) {
      console.error(error);
    }
  };
  

  return (
    <div className="login-form-container">
      <div className="login-card">
        <h3 className="login-form-heading">Daxil Ol</h3>
        <form className="login-form" onSubmit={handleSubmit}>
          <div className="input-container">
            <input
              type="text"
              inputMode="numeric"
              pattern="[0-9]*"
              placeholder="Utis Kod"
              value={username}
              onChange={handleUsernameChange}
              required
            />
          </div>
          <div className="input-container">
            <input
              type={showPassword ? "text" : "password"}
              placeholder="Şifrə"
              value={password}
              onChange={handlePasswordChange}
              required
            />
            <button
              type="button"
              className="show-password-button"
              onClick={handleShowPassword}
            >
              {showPassword ? "Hide" : "Show"}
            </button>
          </div>
          <button type="submit" className="login-form-button">
            Daxil Ol
          </button>
        </form>
      </div>
    </div>
  );
}

export default Loginpage;
