import { useState ,useContext} from 'react';
import { Link } from 'react-router-dom';
import { LoginContext } from './pages/auth/LoginContext';
import "./App.css"

function Navbar(){
    const { isLoggedIn,  handleLogout } = useContext(LoginContext);
    return (
        <nav className="navbar">
            <h1 className="logo">Tərəqqi Liseyi</h1>
            <ul className="nav-links">
            <li><Link to="/">Ana Səhifə</Link></li>
            <li><Link to="/about">Liseyimiz Haqda</Link></li>
            {isLoggedIn && (
                <li><Link onClick={handleLogout}>Çıxış</Link></li>
            )}
            {!isLoggedIn && (
                <li><Link to="/login">Şəxsi Kabinet</Link></li>
            )}
            </ul>
        </nav>
    )
}
export default Navbar;
