import { combineReducers } from "redux";

const loginReducer = (state = false ,action) =>{
    switch(action.type){
        case 'LOG_IN':
            return true;
        case 'LOG_OUT':
            return false;
        default :
            return state
    }
}
const userReducer = (state = "" ,action) =>{
    switch(action.type){
        case 'TEACHER':
            return "TEACHER";
        case 'STUDENT':
            return "STUDENT";
        default :
            return state
    }
}
const reducers = combineReducers({
    login:loginReducer,
})

export default reducers
