import { combineReducers } from "redux";

const loginReducer = (state = false ,action) =>{
    switch(action.type){
        case 'LOG_IN':
            return !state;
        default :
            return state
    }
}

const reducers = combineReducers({
    login:loginReducer
})
export default reducers
